mapper.insert:
insert into e_harvest_records (id, tea_garden_id, dic_standard, 
  rec_quantity, pick_number, pick_batch_no, 
  pick_area, pick_time, operator_id, 
  description, create_id, create_time
  )
values (#{id,jdbcType=VARCHAR}, #{teaGardenId,jdbcType=VARCHAR}, #{dicStandard,jdbcType=VARCHAR}, 
  #{recQuantity,jdbcType=DOUBLE}, #{pickNumber,jdbcType=VARCHAR}, #{pickBatchNo,jdbcType=VARCHAR}, 
  #{pickArea,jdbcType=VARCHAR}, #{pickTime,jdbcType=INTEGER}, #{operatorId,jdbcType=VARCHAR}, 
  #{description,jdbcType=VARCHAR}, #{createId,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT a.*,d.name dicCultiName FROM ( SELECT hr.id, hr.dic_standard dicStandard, hr.tea_garden_id teaGardenId, hr.rec_quantity recQuantity, hr.pick_number pickNumber, hr.pick_area pickArea, hr.pick_time pickTime, hr.operator_id operatorId, hr.create_id createId, tg.area_code areaCode, tg.name_area nameArea, tg.dic_cultivar_id dicCultivarId, d. name dicName, u.chinese_name createName FROM e_harvest_records hr, e_tea_garden tg, s_dictionary d, s_users u WHERE 1 = 1 AND hr.tea_garden_id = tg.id AND hr.dic_standard = d.id AND hr.create_id = u.id
AND hr.tea_garden_id = #{teaGardenId}
AND hr.rec_quantity = #{recQuantity}
AND hr.pick_time = #{pickTime}
)a LEFT JOIN s_dictionary d ON a.dicCultivarId = d.id
