mapper.select:
select
tg.*,d.name teaGradeName ,d1.name gardenTypeName,d2.name cultivarName,d3.name teaIsName
from e_tea_garden tg
LEFT JOIN s_dictionary as d on tg.tea_grade = d.id
LEFT JOIN s_dictionary as d1 on tg.garden_type = d1.id
LEFT JOIN s_dictionary as d2 on tg.cultivar_id = d2.id
LEFT JOIN s_dictionary as d3 on tg.tea_is = d3.id
where 1 = 1

  AND tg.status = #{status}


  AND tg.tea_is = #{teaIs}


  AND tg.cultivar_id = #{cultivarId}


  AND tg.tea_grade = #{teaGrade}


  AND tg.garden_type = #{gardenType}

ORDER BY tg.create_time ASC

SELECT
tg.id, tg.name
FROM e_tea_garden tg
WHERE 1 = 1

  AND tg.status = #{status}


  AND tg.tea_is = #{teaIs}


  AND tg.cultivar_id = #{cultivarId}


  AND tg.tea_grade = #{teaGrade}


  AND tg.garden_type = #{gardenType}

ORDER BY tg.create_time ASC
